mapper.insert:
    		
		    INSERT INTO cart VALUES(
		    	 ctidx
		    	,NOW()
		    	,#{pidx}		
		    	,#{midx}
		    	,#{cnt}
		    	,#{ctsize}
		    	,#{delyn}
		    )
		    
		    		
		    	SELECT MAX(ctidx) FROM cart

    		
		    INSERT INTO wish VALUES(
		    	 widx
		    	,#{pidx}		
		    	,#{midx}
		    )
		    
		    		
		    	SELECT MAX(widx) FROM wish

mapper.update:
    	UPDATE product 
    	SET pNameKr = #{pNameKr}
    		, pNameEng = #{pNameEng}
    		, pBrandKr = #{pBrandKr}
	    	, pBrandEng = #{pBrandEng}
	    	, pSize = #{pSize}
	    	, pColor = #{pColor}
	    	, pGender = #{pGender}
	    	, pType = #{pType}
	    	, pFile1 = #{pFile1}
	    	, pFile2 = #{pFile2}
	    	, pFile3 = #{pFile3}
	    	, pFile4 = #{pFile4}
	    	, pFile5 = #{pFile5}
	    	, pPrice = #{pPrice}
    	WHERE pidx = #{pidx}
