mapper.select:
SELECT COUNT(*) 
FROM wish
WHERE midx=#{midx}

mapper.insert:
		  INSERT INTO cart VALUES(
		    	 ctidx
		    	,NOW()
		    	,#{pidx}		
		    	,#{midx}
		    	,#{cnt}
		    	,#{ctsize}
		    	,#{delyn}
		    )
    		FROM cart c, product p, member m, wish w WHERE c.pidx = p.pidx AND c.midx = m.midx AND w.AND c.ctidx IN
    		#{ctidx}
